SET CLIENT_MIN_MESSAGES = WARNING;BEGIN;  DROP TABLE IF EXISTS public.cart;	CREATE TABLE public.cart	(		id serial,		user_id int NOT NULL REFERENCES "user"(id),		created_at timestamp without time zone DEFAULT now(),	  updated_at timestamp without time zone DEFAULT now(),	  CONSTRAINT cart_pkey PRIMARY KEY (id)	);	DROP TABLE IF EXISTS public.cart_item;	CREATE TABLE public.cart_item	(	  id serial,	  cart_id int NOT NULL REFERENCES "cart"(id),	  product_id int NOT NULL REFERENCES "product"(id),	  quantity int NOT NULL,	  created_at timestamp without time zone DEFAULT now(),	  updated_at timestamp without time zone DEFAULT now(),	  CONSTRAINT cart_item_pkey PRIMARY KEY (id),	  CONSTRAINT cart_product_ukey UNIQUE (cart_id, product_id)	);COMMIT;