SET CLIENT_MIN_MESSAGES = WARNING;BEGIN;	DROP TABLE IF EXISTS public.order_status;	CREATE TABLE public.order_status	(		id serial,		name text not null,		created_at timestamp without time zone DEFAULT now(),	  updated_at timestamp without time zone DEFAULT now(),	  CONSTRAINT order_status_pkey PRIMARY KEY (id)	);		insert into order_status(name) values ('pending');	insert into order_status(name) values ('paid');	insert into order_status(name) values ('refund');		alter table public."order" add column order_status_id int default 1;	alter table public."order" add constraint fk_order_status_id foreign key (order_status_id) references order_status(id);COMMIT;