SET CLIENT_MIN_MESSAGES = WARNING;BEGIN;	DROP TABLE IF EXISTS public.notification_status;		CREATE TABLE public.notification_status	(	  id serial,	  code varchar(30) NOT NULL,	  created_at timestamp without time zone DEFAULT now(),	  updated_at timestamp without time zone DEFAULT now(),	  CONSTRAINT notification_status_pkey PRIMARY KEY (id)	);		INSERT INTO public.notification_status(code) VALUES ('UNREAD');	INSERT INTO public.notification_status(code) VALUES ('READ');	DROP TABLE IF EXISTS public.notification;	CREATE TABLE public.notification	(	  id serial,	  user_id int NOT NULL REFERENCES "user"(id),	  body text NOT NULL,	  status_id int NOT NULL REFERENCES "notification_status"(id) DEFAULT 1,	  created_at timestamp without time zone DEFAULT now(),	  updated_at timestamp without time zone DEFAULT now(),	  CONSTRAINT notification_pkey PRIMARY KEY (id)	);COMMIT;