SET CLIENT_MIN_MESSAGES = WARNING;BEGIN;  	DROP TABLE IF EXISTS public.product_comment;		CREATE TABLE public.product_comment	(	  id serial,	  product_id int NOT NULL REFERENCES "product"(id),	  parent_comment_id int default 0,	  body text,	  user_id int NOT NULL REFERENCES "user"(id),	  created_at timestamp without time zone DEFAULT now(),	  updated_at timestamp without time zone DEFAULT now(),	  CONSTRAINT product_comment_pkey PRIMARY KEY (id)	);		------	  	DROP TABLE IF EXISTS public.product_rating;		CREATE TABLE public.product_rating	(	  id serial,	  product_id int NOT NULL REFERENCES "product"(id),	  user_id int NOT NULL REFERENCES "user"(id),	  rating int default 0,	  created_at timestamp without time zone DEFAULT now(),	  updated_at timestamp without time zone DEFAULT now(),	  check (rating >= 0 AND rating <=5),	  CONSTRAINT product_rating_pkey PRIMARY KEY (id),	  CONSTRAINT product_id_user_id_ukey UNIQUE (product_id, user_id)	);		------		COMMIT;