SET CLIENT_MIN_MESSAGES = WARNING;BEGIN; 	DROP TABLE IF EXISTS public.order_shipping_info;	CREATE TABLE public.order_shipping_info	(	  id serial,	  orders_id int NOT NULL REFERENCES "orders"(id),	  address1 character varying(50) NOT NULL,	  address2 character varying(50),	  firstname character varying(50) NOT NULL,	  lastname character varying(50) NOT NULL,	  email character varying(50) NOT NULL,	  post_code character varying(50) NOT NULL,	  telephone text NOT NULL,	  created_at timestamp without time zone DEFAULT now(),	  updated_at timestamp without time zone DEFAULT now(),	  CONSTRAINT order_shipping_info_pkey PRIMARY KEY (id),	  CONSTRAINT order_shipping_info_orders_id_ukey UNIQUE (orders_id)	);COMMIT;