SET CLIENT_MIN_MESSAGES = WARNING;BEGIN;DROP TABLE IF EXISTS public.api_category;CREATE TABLE public.api_category (  id serial NOT NULL,  name varchar(50) NOT NULL,  created_at timestamp NULL DEFAULT now(),  updated_at timestamp NULL DEFAULT now(),  CONSTRAINT api_category_pkey PRIMARY KEY (id),  CONSTRAINT api_category_name_ukey UNIQUE (name));DROP TABLE IF EXISTS public.api_info;CREATE TABLE public.api_info (    id serial NOT NULL,    action_type varchar(50) NOT NULL,    api_category_id int NOT NULL REFERENCES "api_category"(id),    request varchar,    response varchar,    created_at timestamp NULL DEFAULT now(),    updated_at timestamp NULL DEFAULT now(),    CONSTRAINT api_info_pkey PRIMARY KEY (id));DROP TABLE IF EXISTS public.api_info_header;CREATE TABLE public.api_info_header (    id serial NOT NULL,    api_info_id int NOT NULL REFERENCES "api_info"(id),    name varchar(50) NOT NULL,    value text[] NOT NULL,    created_at timestamp NULL DEFAULT now(),    updated_at timestamp NULL DEFAULT now(),    CONSTRAINT api_info_header_pkey PRIMARY KEY (id),    CONSTRAINT api_info_header_api_info_id_name_ukey UNIQUE (api_info_id, name));COMMIT;