SET CLIENT_MIN_MESSAGES = WARNING;BEGIN;DROP TABLE IF EXISTS public.home_banner;CREATE TABLE public.home_banner(    id          serial       NOT NULL,    image_url   varchar      NOT NULL,    title       varchar(250) NOT NULL,    description text         NOT NULL,    link        varchar      NOT NULL,    active      boolean      not null default false,    created_at  timestamp    NULL     DEFAULT now(),    updated_at  timestamp    NULL     DEFAULT now());COMMIT;