SET CLIENT_MIN_MESSAGES = WARNING;BEGIN;    DROP TABLE IF EXISTS public.user_roles;    CREATE TABLE public.user_roles    (        id         serial,        user_id    int NOT NULL REFERENCES "user" (id),        role_id    int NOT NULL REFERENCES "role" (id),        created_at timestamp without time zone DEFAULT now(),        updated_at timestamp without time zone DEFAULT now(),        CONSTRAINT user_roles_pkey PRIMARY KEY (id),        CONSTRAINT user_roles_ukey UNIQUE (user_id, role_id)    );COMMIT;