SET CLIENT_MIN_MESSAGES = WARNING;BEGIN;    DROP TABLE IF EXISTS public.teacher_subscription;    CREATE TABLE public.teacher_subscription    (        id serial,        teacher_id int NOT NULL REFERENCES "user"(id),        user_id int NOT NULL REFERENCES "user"(id),        started_at timestamp without time zone DEFAULT  now(),        expired_at timestamp without time zone DEFAULT now(),        created_at timestamp without time zone DEFAULT now(),        updated_at timestamp without time zone DEFAULT now(),        CONSTRAINT teacher_subscription_pkey PRIMARY KEY (id),        CONSTRAINT teacher_subscription_ukey UNIQUE (teacher_id)    );COMMIT;