SET CLIENT_MIN_MESSAGES = WARNING;BEGIN;DROP TABLE IF EXISTS public.job;CREATE TABLE public.job(    id          serial,    user_id     int                    NOT NULL REFERENCES "user" (id),    title       character varying(500) NOT NULL,    description text                   NOT NULL,    created_at  timestamp without time zone DEFAULT now(),    updated_at  timestamp without time zone DEFAULT now(),    CONSTRAINT job_pkey PRIMARY KEY (id));DROP TABLE IF EXISTS public.job_category;CREATE TABLE public.job_category(  id serial,  name   character varying(500) NOT NULL,  code character varying(10) NOT NULL,  CONSTRAINT job_category_pkey PRIMARY KEY (id));COMMIT;